function crimson()
{
    element1 = document.getElementById("chatroomtitle");
    element1.style.background = 'crimson';
    element2 = document.getElementById("chatroom");
    element2.style.border = '1px solid crimson';
    element2.style.borderWidth = 'medium';
    element3 = document.getElementById("sendbutton");
    element3.style.background = 'crimson';
    element3.style.borderColor = 'crimson';
    /*$('.msgbox').css('background-color','crimson');not working due to load*/
}

