function sendmsg()
{
    msginput = document.getElementById("msg");
    var msg = msginput.value;
    $.ajax(
    {
       url:"Process/sendchat.php",
       data:{msg},
       success:function(data)
       {
           
       }
    });
    msginput.value = '';
    $('#chatbox').load("index.php");
}